import React from "react";
import styled from "styled-components";

const ButtonComponent = styled.button`
  position: relative;
  display: inline-flex;
  align-items: center;
  justify-content: center;
  text-align: center;
  text-decoration: none;
  vertical-align: middle;
  cursor: ${(props) => (props.disabled ? "not-allowed" : "pointer")};
  user-select: none;
  border-radius: 8px;
  padding: 0
    ${(props) =>
      props.size === "sm"
        ? "3rem"
        : props.size === "md"
        ? "6rem"
        : props.size === "lg"
        ? "10rem"
        : "6rem"};
  height: ${(props) =>
    props.size === "sm"
      ? "40px"
      : props.size === "md"
      ? "50px"
      : props.size === "lg"
      ? "70px"
      : "50px"};

  font-size: ${(props) =>
    props.size === "sm"
      ? "14px" // ขนาดฟอนต์สำหรับปุ่มเล็ก
      : props.size === "md"
      ? "16px" // ขนาดฟอนต์สำหรับปุ่มขนาดกลาง
      : props.size === "lg"
      ? "20px" // ขนาดฟอนต์สำหรับปุ่มใหญ่
      : "16px"}; // ขนาดเริ่มต้นหากไม่กำหนดขนาด

  font-family: "Sarabun", sans-serif;
  font-weight: 550;
  color: ${(props) => (props.disabled ? "#888" : "#0a4682")};
  border: ${(props) => (props.disabled ? "none" : "1px solid #c5def8")};
  background-color: ${(props) =>
    props.disabled
      ? "#e0e0e0" // สีพื้นหลังเมื่อปุ่มถูกปิดการทำงาน
      : props.variant === "light"
      ? "#edf6ff"
      : props.variant === "primary"
      ? "#cfe5ff"
      : "#edf6ff"};

  &:hover {
    background-color: ${(props) =>
      props.disabled
        ? "#e0e0e0" // ไม่เปลี่ยนแปลงสีพื้นหลังเมื่อปุ่มถูกปิดการทำงาน
        : props.variant === "light"
        ? "#e7f4ff"
        : props.variant === "primary"
        ? "#bedbff"
        : "#e1f0ff"};
    color: ${(props) =>
      props.disabled
        ? "#888" // สีของข้อความเมื่อปุ่มถูกปิดการทำงาน
        : props.variant === "primary"
        ? "#042e5f"
        : "#3d79fd"};
  }
`;

const Button = ({
  type,
  variant,
  className,
  size,
  id,
  onClick,
  disabled,
  children,
}) => {
  return (
    <ButtonComponent
      type={type ? type : "button"}
      variant={variant}
      className={className ? `btn-component ${className}` : "btn-component"}
      id={id}
      size={size}
      onClick={!disabled ? onClick : null}
      disabled={disabled}
    >
      {children}
    </ButtonComponent>
  );
};

export default Button;
