import React, { useState } from 'react';
import { useLocation } from 'react-router-dom';

function LoopPart() {
    const { state } = useLocation();
    const partCount = state?.part || 0;
    // สร้าง array สำหรับเก็บข้อมูลของแต่ละ part
    const [partsData, setPartsData] = useState(
        Array.from({ length: partCount }, () => ({
            case: '',
            rangeInput: '',
            typePoint: '',
            option: ''
        }))
    );

    // ฟังก์ชันสำหรับอัปเดตค่าของแต่ละ field ในแต่ละ part
    const handleChange = (index, field, value) => {
        const updatedPartsData = [...partsData];
        updatedPartsData[index][field] = value;
        setPartsData(updatedPartsData);
    };

    // ฟังก์ชันสำหรับการ submit ข้อมูล
    const handleSubmit = async () => {
        try {
            await fetch('http://127.0.0.1:5000/create_sheet', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(partsData),
            });
            alert('ส่งข้อมูลของ Parts สำเร็จ');
        } catch (error) {
            console.error('Error submitting data:', error);
        }
    };

    return (
        <div>
            <h1>Loop Part Inputs</h1>
            {/* Loop สำหรับสร้าง input fields ตามจำนวน Part */}
            {Array.from({ length: partCount }, (_, i) => (
                <div key={i} style={{ marginBottom: '20px' }}>
                    <h2>Part ที่ {i + 1}</h2>
                    <label>Case:</label>
                    <input
                        type="text"
                        value={partsData[i].case}
                        onChange={(e) => handleChange(i, 'case', e.target.value)}
                    />
                    <label>Range Input:</label>
                    <input
                        type="number"
                        value={partsData[i].rangeInput}
                        onChange={(e) => handleChange(i, 'rangeInput', e.target.value)}
                    />
                    <label>Type Point:</label>
                    <input
                        type="text"
                        value={partsData[i].typePoint}
                        onChange={(e) => handleChange(i, 'typePoint', e.target.value)}
                    />
                    <label>Option:</label>
                    <input
                        type="text"
                        value={partsData[i].option}
                        onChange={(e) => handleChange(i, 'option', e.target.value)}
                    />
                </div>
            ))}
            <button onClick={handleSubmit}>Submit</button>
        </div>
    );
}

export default LoopPart;
