import React, { useState, useEffect } from "react";
import "../../css/createExamsheet.css";
import { Card, Carousel } from "antd";
import { LeftOutlined, RightOutlined } from "@ant-design/icons";

const Generate = () => {
  const [images, setImages] = useState([]);

  useEffect(() => {
    // ดึงข้อมูลภาพจาก API
    const fetchImages = async () => {
      try {
        const response = await fetch("http://127.0.0.1:5000/get_images");
        const data = await response.json();
        if (data.status === "success") {
          setImages(data.images);
        }
      } catch (error) {
        console.error("Error fetching images:", error);
      }
    };
    fetchImages();
  }, []);

  return (
    <div>
      <h1 className="Title">Generate Examsheet</h1>
      <Card
        className="card-edit"
        style={{
          width: "100%",
          maxWidth: 800,
          minHeight: 600,
          margin: "0 auto",
          height: "auto",
        }}
      >
        <Carousel
          arrows
          prevArrow={<LeftOutlined style={{ fontSize: '24px', color: '#000' }} />}
          nextArrow={<RightOutlined style={{ fontSize: '24px', color: '#000' }} />}
        >
          {images.map((imgSrc, index) => (
            <div key={index} style={{ textAlign: "center" }}>
              <img
                src={`data:image/png;base64,${imgSrc}`}
                alt={`Generated ${index}`}
                style={{
                  width: "50%",      // ลดขนาดเป็น 50% เพื่อให้เห็นลูกศร
                  height: "auto",
                  display: "block",
                  margin: "0 auto",
                  border: "1px solid #ccc",
                }}
              />
            </div>
          ))}
        </Carousel>
      </Card>
    </div>
  );
};

export default Generate;
